<?php  defined ( '_JEXEC' ) or die ( 'Restricted access' );    class plgVmpaymentLagcartusaveInstallerScript {    	public function postflight($type, $parent) {    		    		if ($type == 'uninstall') return true;        	$this->enablePlugin('vmpayment', 'lagcartusave');     	        	return true;    	}    	function enablePlugin($group, $element) {		    //echo $group;    		$plugin = JTable::getInstance('extension');			    		$id = $plugin->find(array('type' => 'plugin', 'folder' => $group, 'element' => $element));			//echo $id ;            if ($id) {                $plugin->load($id);                $plugin->enabled = 1;  				$plugin->store();            }			    	}    }